function StartBoiler(boilerNumber) {
    log ("Start boiler: " + boilerNumber);

    setState(boilers[boilerNumber],1,false);
}

function StopBoiler(boilerNumber) {
    log ("Stop boiler: " + boilerNumber);

    setState(boilers[boilerNumber],0,false);
}

$(BoilerSwitch).on(function(obj){
    //log("obj_val:" + obj.state.val);
    if (obj.state.val === 0 ) { // turn off all
        //log("Switch off 1");
        //log("Switch off 2");
        setState(boilers[1], 0, false);
        setState(boilers[2], 0, false);
    } else if (obj.state.val == 1 ){
        //log("Switch off 2");
        //log("Switch ON 1");

        setState(boilers[2], 0, false);
        setState(boilers[1], 1, false);
    } else if (obj.state.val == 2 ){
       //log("Switch off 1");
        //log("Switch ON 2");
        setState(boilers[1], 0, false);
        setState(boilers[2], 1, false);
    }
});

function CheckWateringDelay() {
    var obj = getState(gardenWateringDelay);
    var delayVal = obj.val;
    log("Watering:delay=" + delayVal);
    if (delayVal === true) {
        var ts = getState(gardenWateringDelayTimeStamp);
        var now = new Date().getTime();
        if (now >= ts.val) {
            log("Watering:Reset Delay");
            setState(gardenWateringDelay, false, true);
            return true;
        }
        else {
            return false;
        }
    }
    return true;
}

function StartWateringPump(){
    setState(gardenPump, 1, false);
}

function StartWatering(zoneValve, nm) {
    log ("Watering:Start " + nm);
    if (CheckWateringDelay()) {
        var pump;
        pump = getState(gardenPump);
        if (pump.val!=1) {
            setState(gardenPump, 1, false);
        }

        setState(zoneValve,1, false);
    } else {
        log("Watering is prohibited")
    }
}

function StopWatering(zoneValve, nm) {
    log ("Watering: Stop " + nm);
    setState(zoneValve,0, false);
}


